import { auth } from '../firebase'

export const signupUser = async (
    email: string,
    password: string,
    setError: React.Dispatch<React.SetStateAction<string>>
) => {
    // clear any previous errors
    setError('')
    try {
        await auth.createUserWithEmailAndPassword(email, password)
    } catch (e) {
        setError(e.message)
    }
}
